import React, {useRef} from "react";
import axios from "axios";
import {FaTrash, FaEdit} from "react-icons/fa";
import {toast} from "react-toastify";
import { Table, Thead, Tr, Th, Tbody, Td } from "../../styles/styledConsts";

const GridCliente = ({clientes, setCliente, setOnEdit}) => {

    const handleDelete = async(id) => { //Responsável pela exclusão dos produtos. 
        await axios
            .delete(`http://140.238.181.193:3000/clientes/`, {
                data: {id: id}
            })
            .then(({data}) => {
                const newArray = clientes.filter((cliente) => cliente.id !== id);

                setCliente(newArray);//Cadastra a nova instância
                toast.success(data);//Envia a mensagem de sucesso.
            })
            .catch((err) => {
                console.error(err);
                toast.error("Erro ao excluir o cliente");//Envia a mensagem de erro.
            });
            
        setOnEdit(null);
    };

    const handleEdit = (item) => {
        setOnEdit(item);
    };

    return(
        <Table>
            <Thead>
                <Tr>
                    <Th>Nome</Th>
                    <Th>CPF</Th>
                    <Th>Idade</Th>
                    <Th>Cidade</Th>
                    <Th>Time</Th>
                    <Th>E-mail</Th>
                    <Th>Senha</Th>
                    <Th></Th>
                </Tr>
            </Thead>
            <Tbody>
                {clientes.map((item, i) => (
                    <Tr key = {item.id}>
                        <Td width = "30%">{item.nome}</Td>
                        <Td width = "30%">{item.cpf}</Td>
                        <Td width = "30%">{item.idade}</Td>
                        <Td width = "30%">{item.cidade}</Td>
                        <Td width = "30%">{item.time}</Td>
                        <Td width = "30%">{item.email}</Td>
                        <Td width = "30%">{item.senha}</Td>
                        <Td alignCenter width = "5%">
                            <FaEdit onClick={() => handleEdit(item)} style={{ color: "#000", cursor: "pointer" }}/>
                        </Td>
                        <Td alignCenter width = "5%">
                            <FaTrash onClick={() => handleDelete(item.id)} style={{ color: "#000", cursor: "pointer" }}/>
                        </Td>
                    </Tr>
                ))}               
            </Tbody>
        </Table>
    );
};

export default GridCliente;
