import React, { useState, useEffect } from "react";
import FormProduto from "../components/forms/FormProduto";
import GridProduto from "../components/grids/GridCliente";
import styled from "styled-components";
import {toast} from "react-toastify";

const Container = styled.div`
  width: 100%;
  max-width: 800px;
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
`;


const Title = styled.h2``;

const ProdutoPage = () => {

  const[produtos, setProduto] = useState([]);
  const[onEdit, setOnEdit] = useState(null);

  const getProduto = async () => {
    try {
      const response = await fetch("http://140.238.181.193:3000/produtos", {
      
      })
      
      if (!response.ok) {
        throw new Error(`Erro ao buscar os produtos: ${response.statusText}`);
      }
    
      const data = await response.json();
      console.log("Produtos recebidos: ", data);

      setProduto(data.sort((a, b) => (a.nome > b.nome ? 1 : -1)));//Ordena alfabeticamente
    } catch (error) {
      console.error(error);
      toast.error("Erro ao carregar produtos");//Exibe a mensagem de erro
    }
    
  }

  useEffect(() => {
    getProduto();
  }, []);


  return (
    <div>
      <Container>
        <Title>PRODUTOS</Title>
      </Container>
      <FormProduto onEdit={onEdit} setOnEdit={setOnEdit} getProduto = {getProduto} />
      <GridProduto produtos={produtos} setProduto={setProduto} setOnEdit={setOnEdit}/>
      <ToastContainer autoClose = {3000} position = "bottom-left"/>  
      <GlobalStyle/> 
    </div>
  );
};

export default ProdutoPage;
