import React, {useRef, useEffect, useState} from "react";
import styled from "styled-components";
import axios from "axios";
import {toast} from "react-toastify";
import handleUpdate from "./grid";

const FormContainer = styled.form`
    display: flex;
    align-items: flex-end;
    gap: 10px;
    flex-wrap: wrap;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
`;

const InputArea = styled.div`
    display: flex;
    flex-direction: column;
`;

const Button = styled.button`
    padding: 10px;
    cursor: pointer:
    border-radius: 5px:
    border: none;
    background-color: #2c73d2;
    color: white;
    height: 42px;
`;

const Label = styled.label``;

const Input = styled.input`
    width: 120px;
    padding: 0px 10px;
    border: 1px solid #bbb;
    border-radius: 5px;
    height: 40px;
`;


const Form = ({ onEdit, setOnEdit, handleUpdate, getProduto }) => {
    const ref = useRef();
    const [nome, setNome] = useState("");
    const [tipo, setTipo] = useState("");
    const [quantidade, setQuantidade] = useState("");
    const [preco, setPreco] = useState("");
  
    useEffect(() => {
      if (onEdit) {
        setNome(onEdit.nome);
        setTipo(onEdit.tipo);
        setQuantidade(onEdit.quantidade);
        setPreco(onEdit.preco);
      }
    }, [onEdit]);
  
    const handleSubmit = async (e) => {
      e.preventDefault(); 
  
      const updatedProduct = {
        nome,
        tipo,
        quantidade,
        preco,
      };
  
      
      await handleUpdate(onEdit.id, updatedProduct);
  
      
      getProduto();
  
      
      setOnEdit(null);
      setNome("");
      setTipo("");
      setQuantidade("");
      setPreco("");
    };



    return (
        <FormContainer ref = {ref} onSubmit={handleSubmit}>
            <InputArea>
                <Label>Nome</Label>
                <Input name = "nome" onChange={(e) => setNome(e.target.value)}/>
            </InputArea>
            <InputArea>
                <Label>Tipo</Label>
                <Input name = "tipo" onChange={(e) => setTipo(e.target.value)}/>
            </InputArea>
            <InputArea>
                <Label>Quantidade</Label>
                <Input name = "qtd" type = "number" onChange={(e) => setQuantidade(e.target.value)}/>
            </InputArea>
            <InputArea>
                <Label>Pre√ßo</Label>
                <Input name = "preco" type = "number" step = "0.01" min = "0" onChange={(e) => setPreco(e.target.value)}/>
            </InputArea>

            <Button type = "submit">SALVAR</Button>
        </FormContainer>

    );
};

export default Form;
