import React, {useRef} from "react";
import styled from "styled-components";
import axios from "axios";
import {FaTrash, FaEdit} from "react-icons/fa";
import {toast} from "react-toastify";


const Table = styled.table`
    width: 100%;
    background-color: #fff;
    padding: 20px;
    box-shadow: 0px 0px 5px #ccc;
    border-radius: 5px;
    max-width: 800px;
    margin: 20px auto;
    word-break: break-all;
`;

export const Thead = styled.thead``;


export const Tr = styled.tr``;

export const Th = styled.th`
    width: 30%;
    text-align: start;
    border-bottom: 5px solid #ccc;
    padding: 10px 5px;

`;


export const Tbody = styled.tbody``

export const Td = styled.td`
    padding-top: 15px;
    text-align: ${(props) => (props.alignCenter ? "center" : "start")};
    width: ${(props) => (props.width ? props.width : "auto")};

    
`;



const Grid = ({produtos, setProduto, setOnEdit}) => {

    const handleDelete = async(id) => {
        await axios
            .delete(`http://localhost:3000/produtos/${id}`)
            .then(({data}) => {
                const newArray = produtos.filter((produto) => produto.id !== id);

                setProduto(newArray);
                toast.success(data);
            })
            .catch((err) => {
                console.error(err);
                toast.error("Erro ao excluir o produto");
            });
            
        setOnEdit(null);
    };

    const handleEdit = (item) => {
        setOnEdit(item);
    };
    
    return(
        <Table>
            <Thead>
                <Tr>
                    <Th>Nome</Th>
                    <Th>Tipo</Th>
                    <Th>Quantidade</Th>
                    <Th>Pre√ßo</Th>
                    <Th></Th>
                </Tr>
            </Thead>
            <Tbody>
                {produtos.map((item, i) => (
                    <Tr key = {item.id}>
                        <Td width = "30%">{item.nome}</Td>
                        <Td width = "30%">{item.tipo}</Td>
                        <Td width = "30%">{item.quantidade}</Td>
                        <Td width = "30%">{item.preco}</Td>
                        <Td alignCenter width = "5%">
                            <FaEdit onClick={() => handleEdit(item)} style={{ color: "#000", cursor: "pointer" }}/>
                        </Td>
                        <Td alignCenter width = "5%">
                            <FaTrash onClick={() => handleDelete(item.id)} style={{ color: "#000", cursor: "pointer" }}/>
                        </Td>
                    </Tr>
                ))}               
            </Tbody>
        </Table>
    );
};

export default Grid;
